<script setup>
const rotation = ref(-45);
const needlesRef = ref(null);
const iconRef = ref(null);
const hover = ref(false)
const transforms3d = ref('')
const constrain = ref(400);

const leftHoverEffect = ref(0);
const topHoverEffect = ref(0);

const onMoveTransform = (e) => {
    const x = e.clientX;
    const y = e.clientY;
    const box = e.currentTarget.getBoundingClientRect();

    const calcX = -(y - box.y - (box.height / 2)) / constrain.value;
    const calcY = (x - box.x - (box.width / 2)) / constrain.value;

    transforms3d.value = "perspective(100px)"
        + " rotateX(" + calcX + "deg) "
        + " rotateY(" + calcY + "deg) ";

}

const handleMouseLeave = () => {
    rotation.value = rotation.value - 45;

    transforms3d.value = "perspective(100px)"
        + " rotateX(0deg) "
        + " rotateY(0deg) ";
}

const onMoveHoverEffect = (e) => {
    const x = e.clientX;
    const y = e.clientY;
    const box = iconRef.value.getBoundingClientRect();


    leftHoverEffect.value = x - box.left;
    topHoverEffect.value = y - box.top;
}

const handleMouseMove = (event) => {

    onMoveTransform(event);
    onMoveHoverEffect(event);

    const rect = event.currentTarget.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    const x = event.clientX - centerX;
    const y = event.clientY - centerY;

    // Calcul de l'angle de rotation en degrés
    let newRotation = Math.atan2(y, x) * (180 / Math.PI);

    // Ajustement automatique pour éviter les transitions inattendues
    if (newRotation - rotation.value < -180) {
        newRotation = newRotation + 360;
    }

    // Mise à jour de la rotation
    rotation.value = newRotation;
};

const angleToDirection = {
    E: [337.5, 22.5],
    SE: [22.5, 67.5],
    S: [67.5, 112.5],
    SW: [112.5, 157.5],
    W: [157.5, 202.5],
    NW: [202.5, 247.5],
    N: [247.5, 292.5],
    NE: [292.5, 337.5],
};

const shadowClass = computed(() => {
    const normalizedAngle = (rotation.value % 360 + 360) % 360;

    for (const direction in angleToDirection) {
        const [min, max] = angleToDirection[direction];
        console.log(min, normalizedAngle, max)
        if (normalizedAngle >= min && normalizedAngle < max) {
            return direction;
        }
    }

    return 'E';
});

const neddlesShadow = computed(() => {
    const normalizedAngle = (rotation.value % 360 + 360) % 360;

    if(normalizedAngle > 180) {
        return rotation.value + 2;
    }
    return rotation.value - 2;
})
